import React from 'react';
import '../SolutionPage.css';

function ManageBusinessCalls() {
  return (
    <div className="solution-page">
      <h1 className="solution-title">Tips for Managing Business Calls Efficiently</h1>
      <div className="solution-desc">Simple strategies to ensure you never miss an important call and keep your customers happy.</div>
      <div className="solution-section">
        <ul>
          <li>Set up a reliable answering service for your business.</li>
          <li>Use call forwarding and voicemail wisely.</li>
          <li>Train your team to handle calls professionally.</li>
          <li>Keep track of customer requests and follow up promptly.</li>
        </ul>
      </div>
    </div>
  );
}

export default ManageBusinessCalls;
