import React from 'react';
import '../SolutionPage.css';

function BilingualAnswering() {
  return (
    <div className="solution-page">
      <h1 className="solution-title">Why Bilingual Answering Services Matter</h1>
      <div className="solution-desc">Explore the importance of offering support in multiple languages for your business growth.</div>
      <div className="solution-section">
        <p>Providing bilingual support helps businesses reach a wider audience and ensures that all customers feel valued and understood. It can also give your business a competitive edge in diverse markets.</p>
      </div>
    </div>
  );
}

export default BilingualAnswering;
