import React from 'react';
import '../SolutionPage.css';

function AICustomerService() {
  return (
    <div className="solution-page">
      <h1 className="solution-title">How AI is Changing Customer Service</h1>
      <div className="solution-desc">Discover how artificial intelligence is revolutionizing the way businesses handle customer calls and support.</div>
      <div className="solution-section">
        <p>AI-powered answering services are transforming customer support by providing instant, accurate responses to customer inquiries. Businesses can now operate 24/7, reduce wait times, and improve customer satisfaction with the help of AI.</p>
        <p>From handling routine questions to booking appointments, AI is making it easier for companies to deliver exceptional service at any time of day.</p>
      </div>
    </div>
  );
}

export default AICustomerService;
