import React from 'react';
import '../SolutionPage.css';

function TwentyFourSevenCallAnswering() {
  return (
    <div className="solution-page">
      <h1 className="solution-title">5 Benefits of 24/7 Call Answering</h1>
      <div className="solution-desc">Learn why round-the-clock call answering is a game changer for small businesses.</div>
      <div className="solution-section">
        <ul>
          <li>Never miss an important call, even after hours.</li>
          <li>Increase customer trust and loyalty.</li>
          <li>Capture more leads and opportunities.</li>
          <li>Reduce stress for business owners and staff.</li>
          <li>Improve your business reputation with reliable service.</li>
        </ul>
      </div>
    </div>
  );
}

export default TwentyFourSevenCallAnswering;
