import React from 'react';
import './SolutionPage.css';

export default function BilingualAnsweringService() {
  return (
    <div className="solution-page">
      <h1 className="solution-title">Bilingual Answering Service</h1>
      <p className="solution-desc">Rosie answers calls in both English and Spanish, making your business accessible to a wider audience and providing a seamless experience for all customers.</p>
      <div className="solution-section">
        <h2>Benefits of Bilingual Service</h2>
        <ul>
          <li>Serve more customers</li>
          <li>Break language barriers</li>
          <li>Boost customer satisfaction</li>
        </ul>
      </div>
    </div>
  );
}
